import React, { useState } from 'react';

import './ExpenseForm.css'

const ExpenseForm = (props) => {

    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const[ showButton, setButtonBool] = useState(true);

    const titleChangedHandler = (event) => {
        console.log("title changes");
        setEnteredTitle(event.target.value);
    }

    const amountChangedHandler = (event) => {
        console.log("amount changes");
        setEnteredAmount(event.target.value);
    }

    const dateChangedHandler = (event) => {
        console.log("date changes");
        setEnteredDate(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }

        console.log(expenseData);
        props.onSaveExpenseData(expenseData);
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
        buttonClickHandler();
    }


    const buttonClickHandler = (buttonVal) => {
        if(buttonVal) {
            setButtonBool(false);
        } else {
            setButtonBool(true);
        }
    }

    if(showButton) {
        return  <div className="new-expense__controls">
            <div className='new-expense__actions'>
                <button onClick={buttonClickHandler}>Show Form</button>
            </div>
        </div>
    }


    return <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type='text' value={enteredTitle} onChange={titleChangedHandler}></input>
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type='number' min='0.01' step='0.01' value={enteredAmount} onChange={amountChangedHandler}></input>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type='date' min='2019-01-01' max='2022-12-31' value={enteredDate} onChange={dateChangedHandler}></input>
            </div>
        </div>
        <div className='new-expense__actions'>
            <button type='button' onClick>Cancel</button>
            <button type='submit'>Add Expense</button>
        </div>
    </form>
}

export default ExpenseForm
